from activity.models import Schedule

for schedule in Schedule.objects.all():
    s = schedule
    if s.rich_schoolday_activity:
        s.schedule_type = Schedule.ScheduleType.RICH_SCHOOL_DAY
    else:
        s.schedule_type = Schedule.ScheduleType.BREDE_SCHOOL
    s.save()
